		ResolutionInstructions2ScansResult = Memory::PatternScan(exeModule, "68 ?? ?? ?? ?? 50 E8 ?? ?? ?? ?? 66 8B 0D ?? ?? ?? ?? 8D 54 24 ?? 8D 84 24 ?? ?? ?? ?? 52 50 C6 84 24 ?? ?? ?? ?? ?? 66 89 4C 24 ?? E8 ?? ?? ?? ?? 83 C4 ?? 8D 4C 24 ?? 8B F0 51 8D 4C 24 ?? E8 ?? ?? ?? ?? 3B F5 C6 84 24 ?? ?? ?? ?? ?? 74 ?? 8B FE 83 C9 ?? 33 C0 F2 ?? F7 D1 49 51 56 8D 4C 24 ?? E8 ?? ?? ?? ?? 8B 44 24 ?? 8D 54 24 ?? 52 50 8D 4C 24 ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? 51 55 E8 ?? ?? ?? ?? 8B F0 83 C4 ?? 3B F5 75 ?? 6A ?? 8D 4C 24 ?? 88 9C 24 ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 54 24 ?? 8D 8C 24 ?? ?? ?? ?? 52 C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 6A ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 44 24 ?? 8D 4C 24 ?? 50 E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? 51 8D 4C 24 ?? E8 ?? ?? ?? ?? BF ?? ?? ?? ?? 83 C9 ?? 33 C0 B3 ?? F2 ?? F7 D1 49 88 9C 24 ?? ?? ?? ?? 51 68 ?? ?? ?? ?? 8D 4C 24 ?? E8 ?? ?? ?? ?? BF ?? ?? ?? ?? BE ?? ?? ?? ?? B9 ?? ?? ?? ?? 33 D2 F3 ?? 8B C2 BF ?? ?? ?? ?? 0F 95 ?? BE ?? ?? ?? ?? B9 ?? ?? ?? ?? 33 D2 89 6C 24 ?? F3 ?? 0F 95 ?? 8B CA 83 F8 ?? 89 44 24 ?? 89 4C 24 ?? 0F 85 ?? ?? ?? ?? 3B C8 0F 85 ?? ?? ?? ?? 68 ?? ?? ?? ?? 8D 84 24 ?? ?? ?? ?? 68 ?? ?? ?? ?? 50 E8 ?? ?? ?? ?? 66 8B 0D ?? ?? ?? ?? 8D 54 24 ?? 8D 84 24 ?? ?? ?? ?? 52 50 C6 84 24 ?? ?? ?? ?? ?? 66 89 4C 24 ?? E8 ?? ?? ?? ?? 83 C4 ?? 8D 4C 24 ?? 8B F0 51 8D 4C 24 ?? E8 ?? ?? ?? ?? 3B F5 C6 84 24 ?? ?? ?? ?? ?? 74 ?? 8B FE 83 C9 ?? 33 C0 F2 ?? F7 D1 49 51 56 8D 4C 24 ?? E8 ?? ?? ?? ?? 8B 44 24 ?? 8D 54 24 ?? 52 50 8D 4C 24 ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? 51 55 E8 ?? ?? ?? ?? 8B F0 83 C4 ?? 3B F5 75 ?? 6A ?? 8D 4C 24 ?? 88 9C 24 ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 54 24 ?? 8D 8C 24 ?? ?? ?? ?? 52 C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 6A ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 44 24 ?? 8D 4C 24 ?? 50 E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? 51 8D 4C 24 ?? E8 ?? ?? ?? ?? BF ?? ?? ?? ?? 83 C9 ?? 33 C0 B3 ?? F2 ?? F7 D1 49 88 9C 24 ?? ?? ?? ?? 51 68 ?? ?? ?? ?? 8D 4C 24 ?? E8 ?? ?? ?? ?? BF ?? ?? ?? ?? BE ?? ?? ?? ?? B9 ?? ?? ?? ?? 33 D2 F3 ?? 8B C2 BF ?? ?? ?? ?? 0F 95 ?? BE ?? ?? ?? ?? B9 ?? ?? ?? ?? 33 D2 C7 44 24", "68 ?? ?? ?? ?? 50 E8 ?? ?? ?? ?? 66 8B 0D ?? ?? ?? ?? 8D 54 24 ?? 8D 84 24 ?? ?? ?? ?? 52 50 C6 84 24 ?? ?? ?? ?? ?? 66 89 4C 24 ?? E8 ?? ?? ?? ?? 83 C4 ?? 8D 4C 24 ?? 8B F0 51 8D 4C 24 ?? E8 ?? ?? ?? ?? 3B F5 C6 84 24 ?? ?? ?? ?? ?? 74 ?? 8B FE 83 C9 ?? 33 C0 F2 ?? F7 D1 49 51 56 8D 4C 24 ?? E8 ?? ?? ?? ?? 8B 44 24 ?? 8D 54 24 ?? 52 50 8D 4C 24 ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? 51 55 E8 ?? ?? ?? ?? 8B F0 83 C4 ?? 3B F5 75 ?? 6A ?? 8D 4C 24 ?? 88 9C 24 ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 54 24 ?? 8D 8C 24 ?? ?? ?? ?? 52 C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 6A ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 44 24 ?? 8D 4C 24 ?? 50 E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? 51 8D 4C 24 ?? E8 ?? ?? ?? ?? BF ?? ?? ?? ?? 83 C9 ?? 33 C0 B3 ?? F2 ?? F7 D1 49 88 9C 24 ?? ?? ?? ?? 51 68 ?? ?? ?? ?? 8D 4C 24 ?? E8 ?? ?? ?? ?? BF ?? ?? ?? ?? BE ?? ?? ?? ?? B9 ?? ?? ?? ?? 33 D2 F3 ?? 8B C2 BF ?? ?? ?? ?? 0F 95 ?? BE ?? ?? ?? ?? B9 ?? ?? ?? ?? 33 D2 C7 44 24 ?? ?? ?? ?? ?? F3 ?? 0F 95 ?? 8B CA 83 F8 ?? 89 44 24 ?? 89 4C 24 ?? 0F 85 ?? ?? ?? ?? 3B C8 0F 85 ?? ?? ?? ?? 68 ?? ?? ?? ?? 8D 84 24 ?? ?? ?? ?? 68 ?? ?? ?? ?? 50 E8 ?? ?? ?? ?? 66 8B 0D ?? ?? ?? ?? 8D 54 24 ?? 8D 84 24 ?? ?? ?? ?? 52 50 C6 84 24 ?? ?? ?? ?? ?? 66 89 4C 24 ?? E8 ?? ?? ?? ?? 83 C4 ?? 8D 4C 24 ?? 8B F0 51 8D 4C 24 ?? E8 ?? ?? ?? ?? 3B F5 C6 84 24 ?? ?? ?? ?? ?? 74 ?? 8B FE 83 C9 ?? 33 C0 F2 ?? F7 D1 49 51 56 8D 4C 24 ?? E8 ?? ?? ?? ?? 8B 44 24 ?? 8D 54 24 ?? 52 50 8D 4C 24 ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? 51 55 E8 ?? ?? ?? ?? 8B F0 83 C4 ?? 3B F5 75 ?? 6A ?? 8D 4C 24 ?? 88 9C 24 ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 54 24 ?? 8D 8C 24 ?? ?? ?? ?? 52 C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 6A ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? E8 ?? ?? ?? ?? 8D 44 24 ?? 8D 4C 24 ?? 50 E8 ?? ?? ?? ?? 8D 4C 24 ?? C6 84 24 ?? ?? ?? ?? ?? 51 8D 4C 24 ?? E8 ?? ?? ?? ?? BF ?? ?? ?? ?? 83 C9 ?? 33 C0 C6 84 24");

		if (Memory::AreAllSignaturesValid(ResolutionInstructions1ScansResult, false) == true)
		{
			ResolutionInstructions3ScansResult = ResolutionInstructions1ScansResult;
		}
		else if (Memory::AreAllSignaturesValid(ResolutionInstructions2ScansResult, false) == true)
		{
			ResolutionInstructions3ScansResult = ResolutionInstructions2ScansResult;
		}

		if (Memory::AreAllSignaturesValid(ResolutionInstructions3ScansResult) == true)
		{
			spdlog::info("Resolution Widths List Instruction: Address is {:s}+{:x}", sExeName.c_str(), ResolutionInstructions3ScansResult[ResWidthsList] - (std::uint8_t*)exeModule);

			spdlog::info("Resolution Heights List Instruction: Address is {:s}+{:x}", sExeName.c_str(), ResolutionInstructions3ScansResult[ResHeightsList] - (std::uint8_t*)exeModule);

			static std::string sResX = std::to_string(iCurrentResX) + ":" + std::to_string(iCurrentResX) + ":" + std::to_string(iCurrentResX) + ":" + std::to_string(iCurrentResX); // Original string: "800:1024:1280:1600"

			static std::string sResY = std::to_string(iCurrentResY) + ":" + std::to_string(iCurrentResY) + ":" + std::to_string(iCurrentResY) + ":" + std::to_string(iCurrentResY); // Original string: "600:768:960:1200"

			const char* cNewResolutionWidthsList = sResX.c_str();

			const char* cNewResolutionHeightsList = sResY.c_str();

			Memory::Write(ResolutionInstructions3ScansResult[ResWidthsList] + 1, cNewResolutionWidthsList);

			Memory::Write(ResolutionInstructions3ScansResult[ResHeightsList] + 1, cNewResolutionHeightsList);
		}		

		std::uint8_t* AspectRatioInstructionScanResult = Memory::PatternScan(exeModule, "D8 81 ?? ?? ?? ?? 8B 44 24");
		if (AspectRatioInstructionScanResult)
		{
			spdlog::info("Aspect Ratio Instruction: Address is {:s}+{:x}", sExeName.c_str(), AspectRatioInstructionScanResult - (std::uint8_t*)exeModule);

			Memory::WriteNOPs(AspectRatioInstructionScanResult, 6);

			AspectRatioInstructionHook = safetyhook::create_mid(AspectRatioInstructionScanResult, [](SafetyHookContext& ctx)
			{
				FPU::FADD(fNewAspectRatio);
			});
		}
		else
		{
			spdlog::error("Failed to locate aspect ratio instruction memory address.");
			return;
		}

		std::vector<std::uint8_t*> CameraFOVInstructionsScansResult = Memory::PatternScan(exeModule, "8B 8E ?? ?? ?? ?? 51 E8 ?? ?? ?? ?? 8B C8 E8 ?? ?? ?? ?? 8D 94 24 ?? ?? ?? ?? 8D 44 24 ?? 52 50 E8 ?? ?? ?? ?? 8B C8 E8 ?? ?? ?? ?? E9 ?? ?? ?? ?? 8B 9E", "8B 86 ?? ?? ?? ?? 8D 4C 24 ?? 89 44 24");
